import { useNavigate } from 'react-router-dom';
import './Home.css';

function Home() {
  const navigate = useNavigate();
  const animals = [
    { id: 'cat', image: './resource/cat.webp', name: 'Cat' },
    { id: 'dog', image: './resource/dog.webp', name: 'Dog' },
    { id: 'fox', image: './resource/fox.webp', name: 'Fox' }
  ];

  const handleAnimalClick = (animalId) => {
    navigate(`/chat/${animalId}`);
  };

  return (
    <div className="home-container">
      <h1>Choose an Animal to Chat With</h1>
      <div className="animals-grid">
        {animals.map((animal) => (
          <div 
            key={animal.id} 
            className="animal-card"
            onClick={() => handleAnimalClick(animal.id)}
          >
            <img src={animal.image} alt={animal.name} />
            <h2>{animal.name}</h2>
          </div>
        ))}
      </div>
    </div>
  );
}

export default Home;
