import { useState } from 'react';
import { useParams, Link } from 'react-router-dom';
import './ChatPage.css';
import axios from 'axios';

function ChatPage() {
  const { animal } = useParams();
  const [message, setMessage] = useState('');
  const [chatHistory, setChatHistory] = useState([]);

  axios.defaults.xsrfCookieName = 'csrftoken';
  axios.defaults.xsrfHeaderName = 'X-CSRFToken';
  axios.defaults.withCredentials = true;

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (message.trim()) {
      const newMessage = {
        text: message,
        sender: 'user',
        timestamp: new Date().toLocaleTimeString()
      };

      // Add user message
      setChatHistory([...chatHistory, newMessage]);

      try {
        // Updated axios request with more detailed CORS configuration
        const { data } = await axios.post('http://localhost:8000/chat/chat/', {
          animal,
          message
        }, {
          withCredentials: true,
          headers: {
            'Content-Type': 'application/json',
            'Accept': 'application/json',
          }
        });

        // Add animal's response
        setChatHistory(prev => [...prev, {
          text: data.response,
          sender: 'animal',
          timestamp: new Date().toLocaleTimeString()
        }]);
      } catch (error) {
        // More detailed error logging
        console.error('Error details:', {
          message: error.message,
          response: error.response?.data,
          status: error.response?.status
        });

        // Add more specific error message to chat
        setChatHistory(prev => [...prev, {
          text: `Error: ${error.response?.status || 'Unknown'} - ${error.message}`,
          sender: 'animal',
          timestamp: new Date().toLocaleTimeString()
        }]);
      }

      setMessage('');
    }
  };

  return (
    <div className="chat-container">
      <div className="chat-header">
        <Link to="/" className="back-button">‚Üê Back</Link>
        <h2>Chat with {animal}</h2>
      </div>

      <div className="chat-history">
        {chatHistory.map((msg, index) => (
          <div key={index} className={`message ${msg.sender}`}>
            <p>{msg.text}</p>
            <span className="timestamp">{msg.timestamp}</span>
          </div>
        ))}
      </div>

      <form onSubmit={handleSubmit} className="chat-input">
        <input
          type="text"
          value={message}
          onChange={(e) => setMessage(e.target.value)}
          placeholder="Type your message..."
        />
        <button type="submit">Send</button>
      </form>
    </div>
  );
}

export default ChatPage;
