import { useState, useEffect, useCallback } from 'react';
import { useParams, Link } from 'react-router-dom';
import './ChatPage.css';
import axios from 'axios';

// Create axios instance with CSRF handling
// only for local now
const api = axios.create({
  baseURL: 'http://localhost:8000',
  withCredentials: true,
  headers: {
    'Content-Type': 'application/json',
    'Accept': 'application/json',
  }
});

// Add this interceptor to handle CSRF tokens
api.interceptors.request.use(function (config) {
  const csrfToken = document.cookie
    .split('; ')
    .find(row => row.startsWith('csrftoken='))
    ?.split('=')[1];

  if (csrfToken) {
    config.headers['X-CSRFToken'] = csrfToken;
  }
  return config;
});

function ChatPage() {
  const { animal } = useParams();
  const [message, setMessage] = useState('');
  const [chatHistory, setChatHistory] = useState([]);

  const fetchChatHistory = useCallback(async () => {
    try {
      const { data } = await api.get(`/chat/history/`, {
        params: {
          animal_type: animal
        }
      });

      if (data.messages) {
        const formattedHistory = data.messages.map(msg => ({
          text: msg.content,
          sender: msg.role === 'assistant' ? 'animal' : 'user'
        }));

        setChatHistory(formattedHistory);
      }
    } catch (error) {
      console.error('Error fetching chat history:', {
        message: error.message,
        response: error.response?.data,
        status: error.response?.status
      });
    }
  }, [animal]);

  useEffect(() => {
    fetchChatHistory();
  }, [fetchChatHistory]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (message.trim()) {
      const newMessage = {
        text: message,
        sender: 'user',
        timestamp: new Date().toLocaleTimeString()
      };

      // Add user message
      setChatHistory([...chatHistory, newMessage]);

      try {
        // Updated axios request with more detailed CORS configuration
        const { data } = await api.post('/chat/chat/', {
          message,
          animal
        });

        // Add animal's response
        setChatHistory(prev => [...prev, {
          text: data.content,
          sender: 'animal',
          timestamp: new Date().toLocaleTimeString()
        }]);
      } catch (error) {
        // More detailed error logging
        console.error('Error details:', {
          message: error.message,
          response: error.response?.data,
          status: error.response?.status
        });

        // Add more specific error message to chat
        setChatHistory(prev => [...prev, {
          text: `Error: ${error.response?.status || 'Unknown'} - ${error.message}`,
          sender: 'animal',
          timestamp: new Date().toLocaleTimeString()
        }]);
      }

      setMessage('');
    }
  };

  return (
    <div className="chat-container">
      <div className="chat-header">
        <Link to="/" className="back-button">← Back</Link>
        <h2>Chat with {animal}</h2>
      </div>

      <div className="chat-history">
        {chatHistory.map((msg, index) => (
          <div key={index} className={`message ${msg.sender}`}>
            <p>{msg.text}</p>
            <span className="timestamp">{msg.timestamp}</span>
          </div>
        ))}
      </div>

      <form onSubmit={handleSubmit} className="chat-input">
        <input
          type="text"
          value={message}
          onChange={(e) => setMessage(e.target.value)}
          placeholder="Type your message..."
        />
        <button type="submit">Send</button>
      </form>
    </div>
  );
}

export default ChatPage;
